mapper.insert:
INSERT INTO REVIEW (
    rsv_id, rating, content, inst_id, reg_dt
) VALUES (
    #{reservationId}, #{rating}, #{content}, #{userId}, NOW()
)

mapper.select:
SELECT COUNT(*)
FROM REVIEW r
JOIN RESERVATION res ON r.rsv_id = res.rsv_id
WHERE r.rvw_id = #{reviewId}
AND res.user_id = #{userId}

mapper.update:
UPDATE REVIEW r
JOIN RESERVATION res ON r.rsv_id = res.rsv_id
SET 
    r.rating = #{rating},
    r.content = #{content}
WHERE r.rvw_id = #{reviewId}
AND res.user_id = #{userId}

mapper.delete:
DELETE r FROM REVIEW r
JOIN RESERVATION res ON r.rsv_id = res.rsv_id
WHERE r.rvw_id = #{reviewId}
